public static boolean isPalindrome(String s) {
    if (s == null || s.length() < 2) {
        return false;
    }
    int left = 0;
    int right = s.length() - 1;
    while (right >= 0) {
        if (s.charAt(right) != s.charAt(left)) {
            return false;
        }
        left++;
        right--;
    }
    return true;
}
