int binarySearch(int arr[], int n, int x) {
    int low = 0;
    int high = n - 1;
    while (low <= high) {
        int mid = (low + high) / 2;
        if (arr[mid] == x) {
            return mid;
        }
        if (arr[mid] > x) {
            high = mid - 1;
        } else {
            low = mid + 1;
        }
    }
    return -1;
}

class Stack:
    def __init__(self):
        self.items = []
        self.is_empty = True

    def push(self, item):
        if not self.is_empty:
            raise ValueError("Stack is full")
        self.items.append(item)
        self.is_empty = False

    def pop(self):
        if self.is_empty:
            raise IndexError("Stack is empty")
        return self.items.pop()

    def peek(self):
        if not self.is_empty:
            return self.items[0]
        else:
            return None

