void reverseLinkedList(struct ListNode *head) {
    if (head == NULL) {
        return;
    }

    struct ListNode *prev = head;
    struct ListNode *next = head->next;

    while (next != NULL) {
        struct ListNode *temp = next;
        next = prev;
        prev = temp;
    }

    struct ListNode *last = prev;
    prev = last->next;
    last->next = head;

    head = prev;
}

