def longest_increasing_subarray(arr):
    # Initialize variables
    left = 0
    right = len(arr) - 1

    # Find the first and last indices of the longest increasing subarray
    max_length = 0
    current_length = 1

    while left < right:
        # If the current subarray is longer than the previous one, update the max length
        if arr[left] > arr[right]:
            max_length = max(current_length, max_length)
        else:
            current_length = 1

        # Move the boundaries to the left and right edges of the current subarray
        while arr[left] <= arr[right]:
            if arr[left] == arr[right]:
                left += 1
            else:
                right -= 1

        # Update the boundaries according to the maximum length of the subarray found so far
        left = left + current_length - max_length

    return max_length

