def print_longest_common_subsequence(s1, s2):
    # Create a dictionary to store the longest common subsequence
    lcs = {}

    # Create a function to find the longest common subsequence
    def find_lcs(s1, s2, lcs):
        # If either string is empty, return the other string
        if len(s1) == 0 or len(s2) == 0:
            return s2

        # Check for all possible matchings in the first string
        for i in range(len(s1)):
            if s1[i] != s2[i]:
                return find_lcs(s1[:i], s2, lcs)

        # Check for all possible matchings in the second string
        return find_lcs(s1[i:], s2, lcs)

    # Call the function to find the longest common subsequence
    longest_subsequence = find_lcs(s1, s2, [])

    # Print the longest common subsequence
    for substring in longest_subsequence:
        print(substring)

binary_search(arr, target) {
  let left = 0;
  let right = arr.length - 1;

  while (left <= right) {
    const mid = left + right;
    if (arr[mid] === target) {
      return mid;
    } else if (arr[mid] < target) {
      left = mid + 1;
    } else {
      right = mid - 1;
    }
  }
  return -1; // Not found
}
