    public static void QuickSort(int[] arr, int left, int right) {
        if (left < right) {
            int pivotIndex = GetPivotIndex(arr, left, right);
            int pivot = arr[pivotIndex];
            arr[pivotIndex] = arr[right];
            arr[right] = pivot;
