    vector<vector<int>> diagonal_sort(vector<vector<int>>& mat) {
        if (mat.empty()) return mat;
        sort(mat[0], mat[0] + mat[1].size());
        for (int i = 0; i < mat.size() - 1; i++) {
            for (int j = 0; j < mat[i].size(); j++) {
                if (j == 0 || j == mat[i].size() - 1) continue;
                int temp = mat[i][j];
                mat[i][j] = mat[i][mat[i].size() - 1];
                mat[i][mat[i].size() - 1] = temp;
            }
        }
        return mat;
    }
