#include <iostream>
#include <fstream>
using namespace std;
int main() {
    string line;
    int count = 0;
    ifstream file("input.txt");
    if (file.is_open()) {
        while (getline(file, line)) {
            // check if the word exists in the line
            if (line.find("dog") != string::npos) {
                count++;
            }
        }
        file.close();
        cout << "Total number of occurrences of 'dog': " << count << endl;
    } else {
        cout << "Could not open file!" << endl;
    }
    return 0;
}
