def longestCommonSubsequence(s1, s2): 
    """
    :type s1: str
    :type s2: str
    :rtype: int
    """
    m = len(s1)
    n = len(s2)
    dp = [[0] * (n + 1) for _ in range(m + 1)] 
    for i in range(m + 1): 
        dp[i][0] = i 
    for j in range(n + 1): 
        dp[0][j] = j 
    for j in range(1, n + 1): 
        for i in range(1, m + 1): 
            if s1[i - 1] == s2[j - 1]: 
                dp[i][j] = dp[i - 1][j - 1] + 1 
            else: 
                dp[i][j] = max(dp[i - 1][j], dp[i][j - 1]) 
    return dp[m][n]

class Solution(object):
    def binarySearch(self, arr, x):
        left = 0
        right = len(arr) - 1

    while left <= right:
        mid = (left + right) // 2

        if arr[mid] == x:
            return mid
        elif arr[mid] < x:
            left = mid + 1
        else:
            right = mid - 1

    return -1


