func longestCommonSubsequence(s1, s2):
    m = len(s1)
    n = len(s2)

    # Create a 2D array to store the result
    dp = [[] for _ in range(m+1)]
    for i in range(0, m+1):
        dp[i] = [] for j in range(n+1):
            dp[i][j] = ""

    # Initialize the result
    for i in range(0, m+1):
        for j in range(0, n+1):
            if s1[i] == s2[j]:
                dp[i][j] = s1[i]
            else:
                dp[i][j] = ""

    # Find the longest common subsequence
    lcs = ""
    for i in range(0, m+1):
        for j in range(0, n+1):
            if len(dp[i][j]) > len(lcs):
                lcs = dp[i][j]
    return lcs

class Solution(object):
    def binarySearch(self, arr, x):
        low = 0
        high = len(arr) - 1
        while low <= high:
        mid = (low + high) // 2
        if arr[mid] < x:
            low = mid + 1
        else:
            high = mid - 1
        if arr[mid] == x:
        return mid
    else:
    return -1

class Queue:
    def __init__(self):
        self.front = None
        self.rear = None

    def enqueue(self, item):
        if self.front is None:
            self.front = item
            self.rear = item
        else:
            self.rear.next = item
            item.next = None

    def dequeue(self):
        if self.rear is None:
            raise Exception('underflow')
        front = self.front
        self.front = self.rear.next
        self.rear = None

        return front

