                if (i == 0 || j == 0) {
                    dp[i][j] = 0;
                } else {
                    int max = Math.max(dp[i - 1][j], dp[i][j - 1]);
                    if (s1.charAt(i - 1) == s2.charAt(j - 1)) {
                        dp[i][j] = max + 1;
                    } else {
                        dp[i][j] = Math.max(dp[i - 1][j], dp[i][j - 1]);
                    }
                }
            }
        }
        return new StringBuffer(dp[s1.length()][s2.length()]).reverse().toString();
    }
