static void QuickSort(int[] arr)
{
    if (arr.Length < 2)
    {
        return;
    }
    int pivot = arr[arr.Length / 2];
    int left = 0;
    int right = arr.Length - 1;
    while (left <= right)
    {
        while (arr[left] < pivot && left++ < arr.Length) ;
        while (arr[right] > pivot && right-- > 0) ;
