def longest_common_subsequence(s1, s2):
    dp = [[0] * len(s1) for _ in range(len(s2))]
    for i in range(len(s1)):
        for j in range(len(s2)):
            if s1[i] == s2[j]:
                dp[i][j] = 1 + dp[i-1][j]
    return max(dp)

import re

valid_email_regex = r'^[^\s@]+@[^\s@]+\.[^\s@]{1,63}$'

def validate_email(email):
    if not email.startswith('[') and not email.endswith(']'):
        return False

    if not re.match(valid_email_regex, email):
        return False

    return True

