public static void Sort<T>(T[] arr) where T : IComparable<T>
{
    if (arr == null || arr.Length <= 1) return;
    QuickSort(arr, 0, arr.Length - 1);
}
private static void QuickSort<T>(T[] arr, int left, int right) where T : IComparable<T>
{
    if (left == right) return;
    int index = Partition(arr, left, right);
    QuickSort(arr, left, index - 1);
    QuickSort(arr, index + 1, right);
}
private static int Partition<T>(T[] arr, int left, int right) where T : IComparable<T>
{
    int i = left, j = right;
    T pivot = arr[right];
    while (i <= j)
    {
        while (i <= j && arr[i] < pivot) i++;
        while (j > i && arr[j] > pivot) j--;
        if (j > i) Swap(arr, i, j);
        i++;
        j--;
    }
    return j;
}
private static void Swap<T>(T[] arr, int i, int j) where T : IComparable<T>
{
    T temp = arr[i];
    arr[i] = arr[j];
    arr[j] = temp;
}
