class Solution(object):
    def findNthFibonacciNumber(self, n):
        """
        :type n: int
        :rtype: int
        """
        dp = [[0 for x in range(n+1)] for y in range(n+1)]
        for i in range(1, n+1):
            dp[i][0] = i
        for i in range(1, n+1):
            for j in range(1, n+1):
                if i == j:
                    dp[i][j] = dp[i-1][j-1]
                else:
                    dp[i][j] = max(dp[i-1][j], dp[i][j-1]) + dp[i-1][j-2]
        return dp[n][n]

