class Solution(object):
    def search(self, A, x):
        left = 0
        right = len(A) - 1
        while left <= right:
            mid = (left + right) // 2
            if A[mid] == x:
                return mid
            if A[mid] > x:
                right = mid - 1
            else:
                left = mid + 1
        return -1

