def longest_common_subsequence(s1, s2):
    # Initialize two arrays - dp[][i][j] represents the length of the longest subsequence
    # that ends at index i in s1 and begins at index j in s2
    # Also initialize a third array - stores the lengths of the longest subsequence found so far
    dp = [[0 for j in range(len(s2) + 1)] for i in range(len(s1) + 1)]
    max_so_far = [0 for _ in range(len(s2) + 1)]

    for i in range(len(s1) + 1):
        for j in range(len(s2) + 1):
            if s1[i - 1] == s2[j - 1]:
                dp[i][j] = dp[i - 1][j - 1] + 1
            else:
                dp[i][j] = max(dp[i - 1][j], dp[i][j - 1])

            max_so_far[j] = max(max_so_far[j], dp[i][j])

    # Return the length of the longest subsequence found so far
    return max(max_so_far)

import re

def validate_email(email):
    # Define a regular expression pattern for an email address
    pattern = r"[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Z|a-z]{2,}\b"

    # Check if the email address matches the regular expression pattern
    if re.match(pattern, email):
        return True
    else:
        return False
