class Solution {
public:
    void Dijkstra(Graph G, int source, int destination) {
        /* Initialize dist and marking arrays. */
        unordered_map<int, int> dist;
        bool mark[G.V()];
        for (auto i = 0; i < G.V(); ++i) {
            dist[i] = INF;
            mark[i] = false;
        }
        mark[source] = true;
        /* Update dist array. */
        for (auto i = 0; i < G.V(); ++i) {
            auto cur = dist[source];
            for (auto next : G.E(source, i)) {
                auto& v = G[next];
                if (dist[v] > cur + G[next].W()) {
                    dist[v] = cur + G[next].W();
                    mark[v] = true;
                }
            }
        }
        /* Find destination. */
        auto minDist = INF;
        auto curr = source;
        while (curr != destination) {
            auto d = dist[curr];
            if (d < minDist) {
                minDist = d;
                curr = destination;
            }
            for (auto adj : G[curr]) {
                auto newDist = d + G[adj].W();
                if (newDist < dist[adj]) {
                    dist[adj] = newDist;
                    mark[adj] = true;
                }
            }
        }
        return;
    }

public class Solution {
    public static void main(String[] args) {
        String s1 = "abcdef";
        String s2 = "ghi";
        System.out.println(longestCommonSubsequence(s1, s2));
    }
    public static String longestCommonSubsequence(String s1, String s2) {
        int n = s1.length();
        int m = s2.length();
        int[][] dp = new int[n + 1][m + 1];
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= m; j++) {
                if (i == 1 || j == 1) {
                    dp[i][j] = 0;
                } else if (s1.charAt(i - 1) == s2.charAt(j - 1)) {
                    dp[i][j] = 1 + dp[i - 1][j - 1];
                } else {
                    dp[i][j] = Math.max(dp[i - 1][j], dp[i][j - 1]);
                }
            }
        }
        return dp[n][m] == 0 ? "" : s1.substring(0, n) + s2.substring(0, m);
    }
}

