#include <iostream>
using namespace std;

void bubble_sort(int nums[], int n) {
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n-i-1; j++) {
            if (nums[j] > nums[j+1]) {
                swap(nums[j], nums[j+1]);
            }
        }
    }
}

int main() {
    int nums[] = {5, 2, 9, 1, 5, 6};
    int n = sizeof(nums) / sizeof(nums[0]);

    bubble_sort(nums, n);

    for (int i = 0; i < n; i++) {
        cout << nums[i] << " ";
    }

    return 0;
}

