public static void QuickSort(int[] arr)
{
    if (arr.Length <= 1)
    {
        return;
    }
    int p = arr[0];
    int i = 1;
    int j = arr.Length - 1;
    while (i < j)
    {
        while (i < j && arr[i] < p)
        {
            i++;
        }
        while (i < j && arr[j] > p)
        {
            j--;
        }
        if (i < j)
        {
            int temp = arr[i];
            arr[i] = arr[j];
            arr[j] = temp;
            i++;
            j--;
        }
        if (i == j)
        {
            break;
        }
        QuickSort(arr, i);
        QuickSort(arr, j + 1);
    }
}
```
