public static void Sort<T>(T[] arr) where T : IComparable {
    if (arr == null || arr.Length <= 1) {
        return;
    }
    int n = arr.Length;
    T pivot = arr[0];
    int i = 1;
    int j = n - 1;
    (comp(ref pivot, ref arr[i]), comp(ref pivot, ref arr[j])));
    while (i <= j) {
        while (comp(ref pivot, ref arr[i])) i++;
        while (comp(ref pivot, ref arr[j])) j--;
        if (i <= j) {
            T temp = arr[i];
            arr[i] = arr[j];
            arr[j] = temp;
            i++;
            j--;
        }
    }
    quicksort(ref arr, 0, i - 1);
    quicksort(ref arr, i, n - 1);
}
private static bool comp<T>(ref T a, ref T b) {
    return a.CompareTo(b) >= 0;
}
