    public static int GetPivotIndex(int[] arr, int left, int right) {
        int pivotIndex = (left + right) / 2;
        for (int i = left; i <= right; i++) {
            if (arr[i] >= pivotIndex) {
                pivotIndex--;
            }
        }
        return pivotIndex;
    }
