def main():
    def createSudoku(n):
        matrix = [[0 for _ in range(n)] for _ in range(n)]
        for i in range(1, n+1):
            for j in range(1, n+1):
                if i == j:
                    matrix[i-1][j-1] = 1
        return matrix
    def solve(matrix):
        sol = []
        visited = []
        for i in range(1, len(matrix)):
            for j in range(1, len(matrix[0])):
                if matrix[i][j] != 0:
                    sol.append(i)
                    visited.append(j)
        return sol
    #write a function to design a medium-level sudoku puzzle
