[JavaScript code]:
function countSyllables(word) {
  word = word.toLowerCase();
  if(word.length <= 3) { return 1; }
  word = word.replace(/(?:[^laeiouy]es|ed|[^laeiouy]e)$/, '');
  word = word.replace(/^y/, '');
  return word.match(/[aeiouy]{1,2}/g).length;
}
function generateLine(syllables) {
  let line = '';
  let count = 0;
  const words = ['the', 'sun', 'slowly', 'sinks', 'below', 'the', 'horizon', 'now', 'painting', 'the', 'sky', 'a', 'hues', 'of', 'red', 'and', 'gold'];
  while (count < syllables) {
    const word = words[Math.floor(Math.random() * words.length)];
    const wordSyllables = countSyllables(word);
    if (count + wordSyllables > syllables) { continue; }
    line += word + ' ';
    count += wordSyllables;
  }
  return line.trim();
}
function generateSonnet() {
  const lines = [
    [10, 'The sun descends with fiery grace,'],
    [10, 'And paints the sky with golden trace,'],
    [10, 'The world is bathed in warm embrace,'],
    [10, 'As light recedes from this fair place.'],
    [10, 'The birds fall silent, night draws near,'],
    [10, 'The land is hushed, all creatures hear,'],
