)
type Ingredient struct {
	Name   string `json:"name"`
	Amount string `json:"amount"`
}
func main() {
	// Parse the JSON data into a slice of Ingredient structs
	ingredientsJSON := `[{"name": "quinoa", "amount": "1 cup"}, {"name": "almond milk", "amount": "1/2 cup"}, {"name": "coconut flour", "amount": "1/4 cup"}, {"name": "matcha powder", "amount": "1 tsp"}, {"name": "avocado", "amount": "1"}]`
