// Train the model
for epoch in 1...numEpochs {
  var epochLoss: Float = 0
  var epochAccuracy: Float = 0
  var numBatches: Int = 0
  for batch in trainImages.batched(batchSize) {
    let (loss, grad) = valueWithGradient(at: model) { model -> Tensor<Float> in
      let logits = model(batch)
      return softmaxCrossEntropy(logits: logits, labels: trainLabels)
    }
    optimizer.update(&model.allDifferentiableVariables, along: grad)
    epochLoss += loss.scalarized()
    epochAccuracy += accuracy(predictions: model(batch), truths: trainLabels)
    numBatches += 1
  }
  epochLoss /= Float(numBatches)
  epochAccuracy /= Float(numBatches)
  print("Epoch \(epoch): Loss: \(epochLoss), Accuracy: \(epochAccuracy)")
}
// Evaluate the model on test data
let testAccuracy = accuracy(predictions: model(testImages), truths: testLabels)
print("Test Accuracy: \(testAccuracy)")
