				var cellValue = parseInt(table.rows[i].cells[1].innerHTML);
				var diff = Math.abs(cellValue - avg);
				if (diff > maxDiff) {
					maxDiff = diff;
					outlierIndex = i;
				}
			}
			for (var i = 1; i <= 4; i++) {
				if (i == outlierIndex) {
					table.rows[i].style.backgroundColor = "red";
				} else {
					table.rows[i].style.backgroundColor = "white";
				}
			}
