function minJumps(arr) {
  let jumps = 0;
  let maxReach = 0;
  let steps = 0;
  for (let i = 0; i < arr.length - 1; i++) {
    maxReach = Math.max(maxReach, i + arr[i]);
    if (i === steps) {
      jumps++;
      steps = maxReach;
    }
  }
  return jumps;
}

