<?php
function getComplementaryColors($baseColor) {
    //convert base color to HSL
    $hsl = rgbToHsl($baseColor);
    //calculate complementary hue
    $complementaryHue = ($hsl['h'] + 180) % 360;
    //convert complementary hue back to RGB
    $complementaryColor = hslToRgb($complementaryHue, $hsl['s'], $hsl['l']);
    //return complementary color pair
    return array($baseColor, $complementaryColor);
}
//convert RGB color to HSL
function rgbToHsl($color) {
    $r = $color['r'] / 255;
    $g = $color['g'] / 255;
    $b = $color['b'] / 255;
    $max = max($r, $g, $b);
    $min = min($r, $g, $b);
    $h = 0;
    $s = 0;
    $l = ($max + $min) / 2;
    if($max == $min) {
        $h = $s = 0; //achromatic
    } else {
        $d = $max - $min;
        $s = $l > 0.5 ? $d / (2 - $max - $min) : $d / ($max + $min);
        switch($max) {
            case $r:
                $h = ($g - $b) / $d + ($g < $b ? 6 : 0);
                break;
            case $g:
                $h = ($b - $r) / $d + 2;
                break;
            case $b:
                $h = ($r - $g) / $d + 4;
                break;
        }
        $h /= 6;
    }
    return array('h' => $h * 360, 's' => $s, 'l' => $l);
}
//convert HSL color to RGB
function hslToRgb($h, $s, $l) {
    $r = $g = $b = $l;
    $d = $s * $l * min(1 - $l, $l);
    if($d > 0) {
        $c = 2 * $d / (1 - abs(2 * $l - 1));
        $h /= 60;
        if($h < 0) $h += 6;
        if($h < 1) {
            $r += ($g - $b) * $c;
            $g -= $c * (1 - $h);
            $b -= $c;
        } elseif($h < 2) {
            $r += ($g - $b) * $c;
            $g += $c * ($h - 1);
            $b -= $c;
        } elseif($h < 3) {
            $r -= $c * ($h - 2);
            $g += ($b - $r) * $c;
            $b -= $c;
        } elseif($h < 4) {
            $r -= $c * ($h - 2);
            $g += ($b - $r) * $c;
            $b += $c * ($h - 3);
        } elseif($h < 5) {
            $r -= $c * ($h - 4);
            $g -= $c;
            $b += ($g - $r) * $c;
        } elseif($h < 6) {
            $r += ($b - $g) * $c;
            $g -= $c;
            $b += ($g - $r) * $c;
        }
    }
    return array('r' => round($r * 255), 'g' => round($g * 255), 'b' => round($b * 255));
}
//example usage
