namespace NLPDemo
{
    class Program
    {
        static void Main(string[] args)
        {
            // Create a new pipeline with default settings
            var pipeline = new StanfordCoreNLP();
            // Define the text to analyze
            var text = "The quick brown fox jumps over the lazy dog.";
            // Remove punctuation marks
            text = Regex.Replace(text, @"[^\w\s]", "");
            // Create a new annotation object
            var annotation = new Annotation(text);
            // Annotate the text
            pipeline.annotate(annotation);
            // Get the list of tokens
            var tokens = annotation.get(typeof(CoreAnnotations.TokensAnnotation));
            // Loop through each token and output its part of speech
            foreach (CoreLabel token in tokens)
            {
                Console.WriteLine("{0}: {1}", token.originalText(), token.get(typeof(CoreAnnotations.PartOfSpeechAnnotation)));
            }
            Console.ReadLine();
        }
    }
}
