public static String calculateAverages(int decimalPlaces, int[]... arrays) {
    StringBuilder sb = new StringBuilder();
    sb.append("Array\tAverage\n");
    for (int i = 0; i < arrays.length; i++) {
        double sum = 0;
        int count = 0;
        for (int num : arrays[i]) {
